sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;

	c.showOverview = true;

	c.showOverviewDiv = function() {
		c.showOverview = true;
		c.showAppList = false;
		c.showServices = false;
	};

	c.showApplicationList = function () {
		c.showOverview = false;
		c.showAppList = true;
	};

	c.hideApplicationList = function () {
		c.showOverview = true;
		c.showAppList = false;
	};

	c.showServiceList = function () {
		c.showOverview = false;
		c.showServices = true;
	};

	c.hideServiceList = function () {
		c.showOverview = true;
		c.showServices = false;
	};


	function chunk(arr, size) {
		var newArr = [];
		for (var i=0; i<arr.length; i+=size) {
			newArr.push(arr.slice(i, i+size));
		}
		return newArr;
	}

	if(c.options.app_chunk_size != 0 && c.options.app_chunk_size != null) {
		c.chunkedAppData = chunk(c.data.appList, c.options.app_chunk_size);
		c.appsChunked = true;
	}

	if(c.options.services_chunk_size != 0 && c.options.services_chunk_size != null) {
		c.chunkedServiceData = chunk(c.data.serviceList, c.options.services_chunk_size);
		c.servicesChunked = true;
	}

}

