sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(!input) {
		data.applicationCount = getApplicationCount();
		data.serviceCount = getServiceCount();
		data.devEffort = getDevHours();
		data.appList = getAppDetails();
		data.serviceList = getServiceDetails();
	}

	function getApplicationCount() {
		var grApp = new GlideRecord("x_snc_proto_refarc_application");
		grApp.query();
		return grApp.getRowCount();
	}

	function getAppDetails() {
		var apps = [];

		var grApp = new GlideRecord("x_snc_proto_refarc_application");
		grApp.orderBy("name");
		grApp.query();
		while(grApp.next()) {
			apps.push({
				"name":grApp.getDisplayValue(),
				"image": grApp.getDisplayValue("image"),
				"link": grApp.getDisplayValue("link")
			});
		}
		return apps;
	}


	function getServiceCount() {
		var grService = new GlideRecord("x_snc_proto_refarc_service");
		grService.query();
		return grService.getRowCount();
	}

	function getServiceDetails() {
		var services = [];

		var grService = new GlideRecord("x_snc_proto_refarc_service");
		grService.orderBy("name");
		grService.query();
		while(grService.next()) {
			services.push({
				"name":grService.getDisplayValue(),
				"image": grService.getDisplayValue("image"),
				"link": grService.getDisplayValue("link")
			});
		}
		return services;
	}

	function getDevHours() {
		var totalHours = 0.0;

		var grHours = new GlideRecord("time_card");
		grHours.addQuery("task", options.time_card_record);
		grHours.query();
		while(grHours.next()) {			
			totalHours = totalHours + parseFloat(grHours.getValue("total"));
		}

		return totalHours;
	}
})();
